import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import LocationMarker from '../LocationMarker/LocationMarker';
import InfoPanel from '../InfoPanel/InfoPanel';
import RouteVisualizer from '../RouteVisualizer/RouteVisualizer';
import Timeline from '../Timeline/Timeline';
import { useMapContext, MapProvider } from '../../context/MapContext';
import './Map.css';

// Set India's coordinates and default zoom level
const INDIA_CENTER = [20.5937, 78.9629];
const DEFAULT_ZOOM = 5;

// AnimatedPane handles smooth transitions when changing map view
const AnimatedPane = () => {
  const map = useMap();
  const { selectedLocation, mapInstance, setMapInstance } = useMapContext();
  
  useEffect(() => {
    // Store map instance in context
    if (map && !mapInstance) {
      setMapInstance(map);
    }
  }, [map, mapInstance, setMapInstance]);
  
  useEffect(() => {
    if (selectedLocation) {
      map.flyTo(
        [selectedLocation.coordinates.lat, selectedLocation.coordinates.lng], 
        10, 
        { 
          animate: true, 
          duration: 1.5 
        }
      );
    }
  }, [selectedLocation, map]);

  return null;
};

const MapContent = () => {
  const { 
    locations, 
    selectedLocation, 
    locationData, 
    isLoading, 
    selectLocation, 
    selectedRoute,
    selectRoute,
    clearSelections,
    resetMapView
  } = useMapContext();
  
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [showTimeline, setShowTimeline] = useState(true);

  useEffect(() => {
    setIsPanelOpen(!!locationData);
  }, [locationData]);

  const handleLocationClick = (location) => {
    selectLocation(location);
  };

  const handleRouteSelect = (routeId) => {
    selectRoute(routeId);
  };

  const closePanel = () => {
    setIsPanelOpen(false);
    // Add a slight delay before resetting data
    setTimeout(() => {
      clearSelections();
    }, 300);
  };

  const toggleTimeline = () => {
    setShowTimeline(!showTimeline);
  };

  return (
    <div className="map-container">
      <MapContainer
        center={INDIA_CENTER}
        zoom={DEFAULT_ZOOM}
        className="map"
      >
        <TileLayer
          url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
        />
        
        {locations.map(location => (
          <LocationMarker
            key={location.id}
            location={location}
            isSelected={selectedLocation?.id === location.id}
            onClick={() => handleLocationClick(location)}
          />
        ))}
        
        {selectedRoute && <RouteVisualizer route={selectedRoute} />}
        <AnimatedPane />
      </MapContainer>

      <div className="map-controls">
        <button className="control-btn home-btn" onClick={resetMapView}>
          <svg viewBox="0 0 24 24" width="18" height="18">
            <path fill="currentColor" d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/>
          </svg>
        </button>
        <div className="route-controls">
          <button 
            className={`control-btn ${selectedRoute?.id === 'buddhist' ? 'active' : ''}`}
            onClick={() => handleRouteSelect('buddhist')}
          >
            Buddhist Trail
          </button>
          <button 
            className={`control-btn ${selectedRoute?.id === 'mughal' ? 'active' : ''}`}
            onClick={() => handleRouteSelect('mughal')}
          >
            Mughal Architecture
          </button>
          <button 
            className={`control-btn ${selectedRoute?.id === 'temple' ? 'active' : ''}`}
            onClick={() => handleRouteSelect('temple')}
          >
            Temple Route
          </button>
        </div>
      </div>
      
      <button 
        className="timeline-toggle-btn"
        onClick={toggleTimeline}
        title={showTimeline ? "Hide Timeline" : "Show Timeline"}
      >
        <svg viewBox="0 0 24 24" width="20" height="20">
          <path fill="currentColor" d="M13 3c-4.97 0-9 4.03-9 9H1l3.89 3.89.07.14L9 12H6c0-3.87 3.13-7 7-7s7 3.13 7 7-3.13 7-7 7c-1.93 0-3.68-.79-4.94-2.06l-1.42 1.42C8.27 19.99 10.51 21 13 21c4.97 0 9-4.03 9-9s-4.03-9-9-9zm-1 5v5l4.28 2.54.72-1.21-3.5-2.08V8H12z"/>
        </svg>
      </button>

      {showTimeline && <Timeline />}

      <InfoPanel 
        isOpen={isPanelOpen}
        isLoading={isLoading}
        locationData={locationData}
        selectedLocation={selectedLocation}
        onClose={closePanel}
      />
    </div>
  );
};

const Map = () => {
  return (
    <MapProvider>
      <MapContent />
    </MapProvider>
  );
};

export default Map;
