// context/MapContext.js

import React, { createContext, useState, useContext, useEffect } from 'react';
import { fetchPlaceData, fetchRoutes, fetchLocations } from '../services/api';
import { placeholderData } from '../data/placeholderData';
import { routes as routesData } from '../data/routes';

// Create the context
const MapContext = createContext();

// Custom hook to use the map context
export const useMapContext = () => useContext(MapContext);

// Provider component
export const MapProvider = ({ children }) => {
  // State for locations and routes
  const [locations, setLocations] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [locationData, setLocationData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [mapInstance, setMapInstance] = useState(null);

  // Initial data loading
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        console.log("Loading initial data...");
        
        // Try to fetch from API
        let locationsData;
        let routesData;
        
        try {
          // In a real application, uncomment these lines to fetch from API
          locationsData = await fetchLocations();
          routesData = await fetchRoutes();
          console.log("Successfully loaded data from API");
        } catch (apiError) {
          console.log("API fetch failed, using placeholder data", apiError);
          
          // For now, use placeholder data with more locations
          // This includes all the locations we added to data/locations.py
          locationsData = [
            { 
              id: 'taj-mahal', 
              name: 'Taj Mahal', 
              coordinates: { lat: 27.1751, lng: 78.0421 },
              category: 'cultural',
              tags: ["UNESCO Heritage", "Mausoleum", "Mughal", "Architecture", "Marble"]
            },
            { 
              id: 'hampi', 
              name: 'Hampi', 
              coordinates: { lat: 15.3350, lng: 76.4600 },
              category: 'historical',
              tags: ["UNESCO Heritage", "Architecture", "Hindu", "Historical Kingdom", "Ruins"]
            },
            { 
              id: 'delhi', 
              name: 'Delhi', 
              coordinates: { lat: 28.7041, lng: 77.1025 },
              category: 'cultural',
              tags: ["Capital City", "Mughal Architecture", "Colonial Heritage", "UNESCO Sites", "Multicultural"]
            },
            { 
              id: 'konark', 
              name: 'Konark', 
              coordinates: { lat: 19.8876, lng: 86.0945 },
              category: 'religious',
              tags: ["Sun Temple", "UNESCO Heritage", "Sculpture", "Ancient Architecture", "Hindu"]
            },
            { 
              id: 'khajuraho', 
              name: 'Khajuraho Temples', 
              coordinates: { lat: 24.8318, lng: 79.9199 },
              category: 'religious',
              tags: ["UNESCO Heritage", "Hindu Temples", "Jain Temples", "Sculpture", "Medieval"]
            },
            { 
              id: 'ajanta', 
              name: 'Ajanta Caves', 
              coordinates: { lat: 20.5523, lng: 75.7033 },
              category: 'religious',
              tags: ["UNESCO Heritage", "Buddhist", "Cave Paintings", "Rock-Cut Architecture", "Ancient Art"]
            },
            { 
              id: 'ellora', 
              name: 'Ellora Caves', 
              coordinates: { lat: 20.0258, lng: 75.1790 },
              category: 'religious',
              tags: ["UNESCO Heritage", "Hindu Temples", "Buddhist Caves", "Jain Monuments", "Rock-Cut Architecture"]
            },
            { 
              id: 'golden-temple', 
              name: 'Golden Temple', 
              coordinates: { lat: 31.6200, lng: 74.8765 },
              category: 'religious',
              tags: ["Sikhism", "Gurdwara", "Golden Architecture", "Pilgrimage", "Religious Center"]
            },
            { 
              id: 'qutub-minar', 
              name: 'Qutub Minar', 
              coordinates: { lat: 28.5244, lng: 77.1855 },
              category: 'historical',
              tags: ["UNESCO Heritage", "Minaret", "Delhi Sultanate", "Islamic Architecture", "Medieval"]
            },
            { 
              id: 'sanchi-stupa', 
              name: 'Sanchi Stupa', 
              coordinates: { lat: 23.4794, lng: 77.7375 },
              category: 'religious',
              tags: ["UNESCO Heritage", "Buddhist", "Stupa", "Ashoka", "Ancient Architecture"]
            }
          ];
          
          // Use placeholder route data
          routesData = window.routes || [];
        }
        
        console.log(`Loaded ${locationsData.length} locations`);
        setLocations(locationsData);
        setRoutes(routesData);
      } catch (err) {
        setError('Failed to load initial data');
        console.error('Error loading initial data:', err);
      }
    };
    
    loadInitialData();
  }, []);

  // Function to handle location selection
  const selectLocation = async (location) => {
    setIsLoading(true);
    setSelectedLocation(location);
    
    try {
      // In a real application, uncomment to fetch from API
      // const data = await fetchPlaceData(location.id);
      
      // For now, use placeholder data with a simulated delay
      setTimeout(() => {
        setLocationData(placeholderData[location.id]);
        setIsLoading(false);
      }, 500);
    } catch (err) {
      setError('Failed to load location data');
      console.error('Error fetching location data:', err);
      setIsLoading(false);
    }
  };

  // Function to select a route
  const selectRoute = (routeId) => {
    // Toggle route selection
    if (selectedRoute?.id === routeId) {
      setSelectedRoute(null);
    } else {
      const route = routes.find(r => r.id === routeId);
      setSelectedRoute(route);
    }
  };

  // Function to clear selections
  const clearSelections = () => {
    setSelectedLocation(null);
    setSelectedRoute(null);
    setLocationData(null);
  };

  // Function to pan map to a location
  const panToLocation = (location) => {
    if (mapInstance && location) {
      mapInstance.flyTo(
        [location.coordinates.lat, location.coordinates.lng],
        10,
        {
          animate: true,
          duration: 1.5
        }
      );
    }
  };

  // Function to reset map view
  const resetMapView = () => {
    if (mapInstance) {
      const INDIA_CENTER = [20.5937, 78.9629];
      const DEFAULT_ZOOM = 5;
      
      mapInstance.flyTo(
        INDIA_CENTER,
        DEFAULT_ZOOM,
        {
          animate: true,
          duration: 1.5
        }
      );
      
      clearSelections();
    }
  };

  // Create the value object
  const contextValue = {
    locations,
    routes,
    selectedLocation,
    selectedRoute,
    locationData,
    isLoading,
    error,
    mapInstance,
    setMapInstance,
    selectLocation,
    selectRoute,
    clearSelections,
    panToLocation,
    resetMapView
  };

  return (
    <MapContext.Provider value={contextValue}>
      {children}
    </MapContext.Provider>
  );
};

export default MapContext;
