import React from 'react';
import { format } from 'date-fns';

const MessageGroup = ({ messages, type }) => {
  const formatTime = (date) => {
    return format(new Date(date), 'h:mm a');
  };

  return (
    <div className={`message-group ${type}`}>
      {messages.map((message, index) => (
        <div key={message.id} className="message-wrapper">
          <div className="message">
            <div className="message-content">
              {message.text}
              {message.media && (
                <div className="message-media">
                  {message.media.type === 'image' && (
                    <img src={message.media.url} alt={message.media.description} />
                  )}
                  {message.media.type === 'map' && (
                    <div className="message-map">
                      {/* Map preview component would go here */}
                    </div>
                  )}
                </div>
              )}
            </div>
            <div className="message-meta">
              <span className="message-time">{formatTime(message.timestamp)}</span>
              {type === 'user' && (
                <span className={`message-status ${message.status}`}>
                  {message.status === 'sent' && '✓'}
                  {message.status === 'delivered' && '✓✓'}
                  {message.status === 'read' && '✓✓'}
                </span>
              )}
            </div>
          </div>
          {message.suggestions && (
            <div className="message-suggestions">
              {message.suggestions.map((suggestion, i) => (
                <button
                  key={i}
                  className="suggestion-chip"
                  onClick={() => message.onSuggestionClick(suggestion)}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default MessageGroup;
