// components/InfoPanel/InfoPanel.jsx

import React, { useState, useEffect } from 'react';
import './InfoPanel.css';

const InfoPanel = ({ isOpen, isLoading, locationData, selectedLocation, onClose }) => {
  const [activeTab, setActiveTab] = useState('history');
  const [isVisible, setIsVisible] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [animationClass, setAnimationClass] = useState('');
  const [showHistoricalView, setShowHistoricalView] = useState(false);

  // Sync visibility state with isOpen prop
  useEffect(() => {
    if (isOpen) {
      setIsVisible(true);
      // Add a small delay to trigger animation after component is mounted
      setTimeout(() => {
        setAnimationClass('animate-in');
      }, 50);
    } else {
      // Set exit animation
      setAnimationClass('animate-out');
      // Delay hiding to allow for exit animation
      setTimeout(() => {
        setIsVisible(false);
        setAnimationClass('');
      }, 300);
    }
  }, [isOpen]);

  // Reset image loaded state when location changes
  useEffect(() => {
    setImageLoaded(false);
  }, [selectedLocation]);

  // Reset to history tab when a new location is selected
  useEffect(() => {
    if (selectedLocation) {
      setActiveTab('history');
    }
  }, [selectedLocation]);

  if (!isVisible && !isOpen) {
    return null;
  }

  const handleTabChange = (tab) => {
    // Add tab change animation
    setAnimationClass('tab-change');
    setTimeout(() => {
      setActiveTab(tab);
      setAnimationClass('animate-in');
    }, 150);
  };

 // ...existing code...
  const renderTabContent = () => {
    if (isLoading) {
      return (
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading information...</p>
        </div>
      );
    }

    if (!locationData) {
      return null;
    }

    switch (activeTab) {
      case 'history':
        return (
          <div className={`tab-content ${animationClass}`}>
            <h3>Historical Significance</h3>
            <p>{locationData.history}</p>
            <div className="time-period">
              <h4>Time Period</h4>
              <div className="time-period-info">
                <span className="period">{locationData.period}</span>
                <span className="dynasty">{locationData.dynasty}</span>
              </div>
            </div>
          </div>
        );
      case 'culture':
        return (
          <div className={`tab-content ${animationClass}`}>
            <h3>Cultural Facts</h3>
            {locationData.culturalFacts && locationData.culturalFacts.length > 0 ? (
              <ul className="culture-facts">
                {locationData.culturalFacts.map((fact, index) => (
                  <li key={index} className={`stagger-delay-${index % 5 + 1}`}>{fact}</li>
                ))}
              </ul>
            ) : (
              <p>No cultural facts available for this location.</p>
            )}
          </div>
        );
      case 'stories':
        return (
          <div className={`tab-content ${animationClass}`}>
            <h3>Legends & Stories</h3>
            {locationData.legends && locationData.legends.length > 0 ? (
              <div className="story-container">
                {locationData.legends.map((legend, index) => (
                  <div className={`story stagger-delay-${index + 1}`} key={index}>
                    <h4>{legend.title || 'Legend'}</h4>
                    <p>{legend.description}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No legends or stories available for this location.</p>
            )}
          </div>
        );
      default:
        return null;
    }
  };
// ...existing code...

  const panelClasses = `info-panel ${isOpen ? 'open' : ''} ${animationClass}`;

  return (
    <div className={panelClasses} aria-hidden={!isOpen}>
      <div className="panel-header">
        {selectedLocation && (
          <>
            <div className="location-image-container">
              <img
                src={showHistoricalView 
                  ? `/assets/images/historical/${selectedLocation.id}.jpg` 
                  : `/assets/images/${selectedLocation.id}.jpg`}
                alt={showHistoricalView ? `Historical ${selectedLocation.name}` : selectedLocation.name}
                className={`location-image ${imageLoaded ? 'loaded' : ''}`}
                onLoad={() => setImageLoaded(true)}
                onError={(e) => {
                  // Fallback to modern image if historical isn't available
                  if (showHistoricalView) {
                    e.target.src = `/assets/images/${selectedLocation.id}.jpg`;
                    console.log("Historical image not found, falling back to modern view");
                  }
                }}
              />
              {showHistoricalView && (
                <div className="historical-badge">
                  Historical View ({selectedLocation.period})
                </div>
              )}
              <div className="image-overlay"></div>
            </div>
            <h2 className="location-title">{selectedLocation.name}</h2>
            <div className="view-toggle">
              <button 
                className={`view-toggle-btn ${showHistoricalView ? 'active' : ''}`}
                onClick={() => setShowHistoricalView(!showHistoricalView)}
              >
                {showHistoricalView ? 'Modern View' : 'Historical View'}
              </button>
            </div>
          </>
        )}
        <button 
          className="close-button" 
          onClick={onClose}
          aria-label="Close information panel"
        >
          <span>×</span>
        </button>
      </div>

      <div className="panel-tabs">
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => handleTabChange('history')}
          aria-pressed={activeTab === 'history'}
        >
          History
        </button>
        <button 
          className={`tab-button ${activeTab === 'culture' ? 'active' : ''}`}
          onClick={() => handleTabChange('culture')}
          aria-pressed={activeTab === 'culture'}
        >
          Culture
        </button>
        <button 
          className={`tab-button ${activeTab === 'stories' ? 'active' : ''}`}
          onClick={() => handleTabChange('stories')}
          aria-pressed={activeTab === 'stories'}
        >
          Stories
        </button>
      </div>

      <div className="panel-content">
        {renderTabContent()}
      </div>

      {locationData && (
        <div className="panel-footer">
          <div className="tags">
            {locationData.tags.map((tag, index) => (
              <span key={index} className="tag">{tag}</span>
            ))}
          </div>
          <button className="explore-more-btn">
            Explore More
          </button>
        </div>
      )}
    </div>
  );
};

export default InfoPanel;
